							"type": "authorPaper",
							"text": "Self-Supervised Modular Architecture for Multi-Sensor Anomaly Detection and Localization",
							"articleLocation": "pdfs/CAI2024-2fo5urY39gdhGmFJimhucB/540900b271/540900b271.pdf",
